import React, { Component,Fragment} from 'react';
import { connect } from 'react-redux';
import { Image,Badge,Col,Row, Button } from 'react-bootstrap';
import { Card } from 'react-bootstrap/cjs';
class Vote extends Component {

    render() { 
        const {question,authorAvatar,isError,userName} = this.props
        return (
        <Fragment>
        <Card className="bg-secondary text-white">
        <Card.Title>{userName}</Card.Title>
        <Image style={{ width:'75px',height:'130px',backgrounColor:'#fff'}} rounded src={authorAvatar!== 'undefined' ? authorAvatar : 'logo'}/>
        </Card>
         </Fragment>
        );
    }
}
function mapStateToProps({authedUser, questions, users},{match}) {

    const userName = users[questions[match.params.question_id].author].name
    const id = match.params.question_id
    const question = questions[id]
    const authorAvatar = users[question.author].avatarURL
    return {
        id,
        question,
        authedUser,
        userName,
        authorAvatar,
      
    }
}

export default connect(mapStateToProps)(Vote);
