import React, { Component,Fragment} from 'react';
import { connect } from 'react-redux';
import { Image,Badge,Col,Row, Button, Form } from 'react-bootstrap';
import { Card } from 'react-bootstrap/cjs';
import { withRouter } from 'react-router-dom'
import QuestionCard from './QuestionCard';

class CardVote extends Component {

    render() {
      const {question,author,id} = this.props
      const { optionOne, optionTwo } = question;
      const { name, avatarURL } = author;
     
        return(
         <div className="card">
          <div className="card-header">
            {name}
          </div>
          <div className="row">
            <div className="col col-md-4">
            <img
                  src={avatarURL}
                  className='card-img'
                  alt={name}
                  />
             
            </div>
            {this.props.isAnswered?
           <div></div>
            : <div className="col col-md-4">
            <Form style={{marginTop:'70px'}}>
            <Form.Check
             type="radio"
             label={optionOne.text}
             name={optionOne}
             id="optionOne"
             style={{marginBottom:'30px'}}
           />
           <Form.Check
             type="radio"
             label={optionTwo.text}
             name={optionTwo}
             id="optionTwo"
          />
          <Button type="submit">Vote</Button>
            </Form>
             </div> }
          </div>
         </div> 
            )
        
    
    }
  }
  
  function mapStateToProps ({ authedUser, questions, users }, props) {
    const id = props.match.params.id
    const question = questions[id];
    const author = users[question.author]
    const isAnswered = !!question ? (question.optionOne.votes.includes(authedUser) || question.optionTwo.votes.includes(authedUser)) : false;
    return {
      id,
      isAnswered,
       author,
       question,
       authedUser
    }
  }
  
  export default connect(mapStateToProps)(CardVote);
