import React, { Component,Fragment} from 'react'
import { Image,Badge,Col,Row, Button } from 'react-bootstrap';
import { Card } from 'react-bootstrap/cjs';
import { connect } from 'react-redux'
import { withRouter} from 'react-router-dom'
import { Link} from 'react-router-dom'
class QuestionCard extends Component {
/*handle=(e,id)=>{
    e.preventDefault()  
  this.props.history.push({pathname: `/questions/${id}`})
  console.log(this.props.history);
    
}*/
test=(e,id)=>{
e.preventDefault() 
this.props.history.push({pathname: `/questions/${id}`})
console.log(id);
}
render() { 
    const {question,author,id} = this.props
    const { optionOne, optionTwo } = question;
    const { name, avatarURL } = author;
    return (
        <Fragment>
          
    <Row xs={1} md={3} className="g-4">
    <Col>
    <Card className="bg-secondary text-white">
    <Card.Title>{name}</Card.Title>
    <Image style={{ width:'75px',height:'130px',backgrounColor:'#fff'}} rounded src={avatarURL!== 'undefined' ? avatarURL : 'logo'}/>
    </Card>
    </Col>
    <Col>
    {<Card style={{ padding:'15px'}}>
        <Card.Text style={{ fontSize:'25px',fontWeight:'bold'}}>
            
                    Would you rather <Badge bg="secondary">!</Badge>
            
        </Card.Text>
        <Card.Text className="text-muted">{optionOne.text}</Card.Text>
        <small bg="secondary">OR</small>
        <Card.Text className="text-muted">{optionTwo.text}</Card.Text>
        <Button variant="dark" onClick={(e)=>this.test(e,id)} ><Card.Link to={`/questions/${id}`} style={{ color:'#fff' , textDecoration: 'none'}}> Open</Card.Link></Button>
    </Card>  
    }
    <Link to={`/questions/${id}`} style={{ color:'#100' , textDecoration: 'none'}}> Open</Link>
    </Col>

    </Row>
        </Fragment>

    );
    }
}
function mapStateToProps ({ questions, users,authedUser }, { id }) {
    const question = questions[id]
    const author = users[question.author]
    return {
       author,
       question,
       authedUser
    }
  }
export default  withRouter(connect(mapStateToProps)(QuestionCard));
