import React, { Component } from "react";
import { connect } from "react-redux";
import { Image } from "react-bootstrap";
class UserCard extends Component {
  styleAuthed = {
    backgrounColor: "#000",
  };
  render() {
    const { user, me } = this.props;
    const { name, avatarURL, answers, questions } = user;
    return (
      <div className="d-flex justify-content-center">
        <div className="card" style={{ marginTop: "30px" }}>
          <div className="row">
            <h4 style={me ? this.styleAuthed : null}>{name}</h4>
            <div className="col col-md-6">
              <Image
                style={{
                  width: "75px",
                  height: "130px",
                  backgrounColor: "#fff",
                }}
                rounded
                src={avatarURL !== "undefined" ? avatarURL : "logo"}
              />
            </div>
            <div className="col col-md-6">
              <div className="card-text">
                Answered Questions: {Object.keys(answers).length}
              </div>
              <div className="card-text">
                Created Questions: {questions.length}
              </div>
              <br />
              <div className="card-footer">
                Score: {Object.keys(answers).length + questions.length}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
function mapStateToProps({ users, authedUser }, { id }) {
  const user = users[id];
  const me = user.id === authedUser;
  return {
    user,
    me,
  };
}

export default connect(mapStateToProps)(UserCard);
