import React, { Component,Fragment,Link} from 'react'
import { Image,Badge } from 'react-bootstrap';
import { Card } from 'react-bootstrap/cjs';
import { connect } from 'react-redux'
class QuestionCard extends Component {

    render() { 
        const {question,author,authedUser,id} = this.props
        const { optionOne, optionTwo, timestamp } = question;
		const { name, avatarURL } = author;
        return (
          <Fragment>
            <Card  style={{width:'150px',height:'150px'}}>
                <div className="question-container">
                 <Card.Body>
                 <Card.Title>{name}</Card.Title>    
                <Image style={{ width:'75px',height:'75px',backgrounColor:'#fff'}} rounded src={avatarURL!== 'undefined' ? avatarURL : 'logo'}/>
               </Card.Body>
               </div>
            </Card>
        
                 <div className='question-containerTwo'>
               <h2>
                        Would you rather <Badge bg="secondary">!</Badge>
               </h2>
                </div>
         
         </Fragment>
       
        );
    }
}
function mapStateToProps ({ questions, users,authedUser }, { id }) {
    const question = questions[id]
    const author = users[question.author]
    return {
       author,
       question,
       authedUser
    }
  }
export default connect(mapStateToProps)(QuestionCard);
