import React from "react";
import { setAuthedUser } from '../actions/authedUser';
import { connect } from 'react-redux'
import 'bootstrap/dist/css/bootstrap.min.css'


class SignupForm extends React.Component {
    handleChange=(e)=>{
    const userID = this.userID.value;
    }

    handleSubmit = (e) => {
        const userID = this.userID.value;
        const { dispatch } = this.props;
        e.preventDefault();

        if (userID !== '') {
            dispatch(setAuthedUser(userID));
        } else{
            alert('you must choose userName')
        }
    };

    render() { 
        const { userNames } = this.props;
        return (
            <form >
                <h1>join our community !</h1>
                <div className="form-group">
                    <label className="control-label">Username</label>
                    <select 
                    name="username" 
                    className="form-conrol" 
                    onChange={this.handleChange}
                     
                    ref={(id) => (this.userID = id)}
                    >
                    <option value="" disabled>choose your user name</option>
                    {userNames.map((item) => (
						<option value={item.value} key={item.value}>
							{item.label}
						</option>
					))}
                
                    </select>
                </div>
                <div className="form-group">
                    <button className="btn btn-primary btn-lg"onClick={(e)=>this.handleSubmit(e)}>
                     Login
                    </button>
                </div>
            </form>
        );
    }
}
function mapStateToProps({ users }) {
	return {
		userNames: Object.keys(users).map((id) => ({
			value: id,
			label: users[id].name
		}))
	};
}
export default connect(mapStateToProps)(SignupForm);
